package com.twitter.matchmaker

import java.util.{List => JList, Map => JMap, Set => JSet}
import scala.collection._
import scala.collection.JavaConversions._

// Autogenerated

object MatchmakerException {
  def apply(thrifty: com.twitter.matchmaker.thrift.MatchmakerException) = new MatchmakerException(thrifty)
}

case class MatchmakerException(description: String) extends Exception{
  def this(thrifty: com.twitter.matchmaker.thrift.MatchmakerException) = this(
    
      
      
        thrifty.description
      
    
  )

  

  def toThrift() = {
    val out = new com.twitter.matchmaker.thrift.MatchmakerException()
    
      
        out.setDescription(description)
      
    
    out
  }
}
